import { useState } from "react";

function Betting() {
  const [selected, setSelected] = useState(""); // track who is selected

  const handleSelect = (name) => {
    setSelected(name);
  };

  return (
    <div className="container mt-5">
      <h2 className="text-light">Place Your Bets</h2>
      <ul className="list-group">
        <li
          className={`list-group-item ${selected === "SnowWolf" ? "active" : ""}`}
          onClick={() => handleSelect("SnowWolf")}
        >
          SnowWolf — $9,800
        </li>
        <li
          className={`list-group-item ${selected === "PipeMaster" ? "active" : ""}`}
          onClick={() => handleSelect("PipeMaster")}
        >
          PipeMaster — $7,200
        </li>
        <li
          className={`list-group-item ${selected === "MoneyRunner" ? "active" : ""}`}
          onClick={() => handleSelect("MoneyRunner")}
        >
          MoneyRunner — $5,400
        </li>
      </ul>

      {selected && <p className="text-light mt-3">You selected: {selected}</p>}
    </div>
  );
}

export default Betting;
