// src/Components/Betting.js
import React, { useState, useEffect } from "react";

function Betting() {
  // Wallet state: stores user's current balance
  const [wallet, setWallet] = useState(() => {
    // Load wallet from localStorage if exists, otherwise start with $1000
    const saved = localStorage.getItem("wallet");
    return saved ? JSON.parse(saved) : 1000;
  });

  // Loan state: stores current loan balance
  const [loan, setLoan] = useState(0);

  // Bet amount state
  const [betAmount, setBetAmount] = useState("");

  // Alert message state
  const [alert, setAlert] = useState("");

  // Persist wallet to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("wallet", wallet);
  }, [wallet]);

  // Handle placing a bet
  const handleBet = () => {
    const amount = parseInt(betAmount);

    if (!amount || amount <= 0) {
      setAlert("Please enter a valid bet amount!");
      return;
    }

    if (amount > wallet) {
      setAlert("You cannot bet more than your wallet balance!");
      return;
    }

    // Random win/loss: 50% chance
    const won = Math.random() < 0.5;
    const newBalance = won ? wallet + amount : wallet - amount;

    setWallet(newBalance);
    setAlert(won ? `You won $${amount}!` : `You lost $${amount}.`);

    setBetAmount(""); // reset input
  };

  // Handle taking a standard 2x-interest loan
  const takeLoan = () => {
    if (loan > 0) {
      setAlert("You already have an outstanding loan!");
      return;
    }
    const loanAmount = 500; // fixed loan
    setWallet(wallet + loanAmount);
    setLoan(loanAmount * 2); // 2x payback
    setAlert(`Loan of $${loanAmount} added! You must repay $${loanAmount * 2}.`);
  };

  // Handle taking a high-interest 4x loan
  const takeHighInterestLoan = () => {
    if (loan > 0) {
      setAlert("You already have an outstanding loan!");
      return;
    }
    const loanAmount = 250; // smaller loan
    setWallet(wallet + loanAmount);
    setLoan(loanAmount * 4); // 4x payback
    setAlert(`High-interest loan of $${loanAmount} added! You must repay $${loanAmount * 4}.`);
  };

  // Handle loan repayment
  const repayLoan = () => {
    if (loan === 0) {
      setAlert("You have no loan to repay!");
      return;
    }
    if (wallet >= loan) {
      setWallet(wallet - loan);
      setAlert(`Loan of $${loan} repaid successfully!`);
      setLoan(0);
    } else {
      setAlert("Insufficient funds to repay loan!");
    }
  };

  return (
    <div className="container mt-5">
      <br /><br /><br />
      <h2 className="text-white">Snowboarding Betting Arena</h2>

      {/* Wallet Display */}
      <p className="wallet">Wallet: ${wallet}</p>
      {loan > 0 && <p className="text-warning">Outstanding Loan: ${loan}</p>}

      {/* Alert Message */}
      {alert && <div className="alert alert-info">{alert}</div>}

      {/* Betting Form */}
      <div className="card p-4 mb-3">
        <h5>Place a Bet</h5>
        <input
          type="number"
          className="form-control mb-2"
          placeholder="Enter bet amount"
          value={betAmount}
          onChange={(e) => setBetAmount(e.target.value)}
        />
        <button className="btn btn-outline-primary me-2" onClick={handleBet}>
          Bet
        </button>
      </div>

      {/* Loan Section */}
      <div className="card p-4 mb-3">
        <h5>Take a Loan</h5>
        <button className="btn btn-success me-2" onClick={takeLoan}>
          Standard Loan (2x Payback)
        </button>
        <button className="btn btn-warning me-2" onClick={takeHighInterestLoan}>
          High-Interest Loan (4x Payback)
        </button>
        <button className="btn btn-danger" onClick={repayLoan}>
          Repay Loan
        </button>
      </div>

      {/* Warning if wallet is zero */}
      {wallet <= 0 && (
        <div className="alert alert-danger">
          Your wallet is empty! Take a loan to continue betting.
        </div>
      )}
    </div>
  );
}

export default Betting;
